import styled from "styled-components";

export const ContainerNavegador = styled.div`
  width: 100vw;
  height: 20vh;
  background-color: blue;
  display: flex;
`

export const StyledText = styled.p`
  font-size: 16px;      
  font-family: 'Arial, sans-serif';
  color: #333;          
  line-height: 1.5; 
  text-align: left;      
  margin: 20px 0;      
  padding: 10px;         /* Preenchimento */
  font-weight: 400;      /* Peso da fonte */
  letter-spacing: 0.5px; /* Espaçamento entre letras */
  text-transform: none;  /* Transformação do texto (ex: uppercase, lowercase) */
  text-decoration: none; /* Decoração do texto (ex: underline, line-through) */

  /* Estilos responsivos */
  @media (max-width: 768px) {
    font-size: 14px;     /* Tamanho da fonte para telas menores */
    text-align: center;  /* Alinhamento do texto para telas menores */
  }
`;
