import React, { useRef, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

import '../../styles.css';

import { Pagination, Navigation } from 'swiper/modules';

import { CustomSwiperSlide, AddButton, SlideContent, Form } from './CarroselStyle';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

export function SwiperNav() {
  let appendNumber = 4;
  let prependNumber = 1;
  const [swiperRef, setSwiperRef] = useState(null);
  const [nomedoc, setNomeDoc] = useState('');
  const [link, setLink] = useState('');

  const handleAddPage = () => {
    const nomeDocInput = prompt('Digite o nome da p치gina:');
    const linkInput = prompt('Digite o link da p치gina:');
    if (nomeDocInput && linkInput) {
      setNomeDoc(nomeDocInput);
      setLink(linkInput);
      handleSubmit();
    }
  };

  const handleSubmit = async () => {
    const accessToken = localStorage.getItem('accessToken');
    const decodedToken = jwtDecode(accessToken);
    const email = decodedToken.sub;
    try {
      const response = await axios.patch(
        `https://gerenciador-tarefas-yuip.onrender.com/v1/users/add-doc?email=${email}`,
        { nomedoc, link },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

    } catch (error) {
      if (error.response) {
        alert('Erro ao fazer login: ' + error.response.data.message);
      } else if (error.request) {
        alert('Erro ao fazer login: Sem resposta do servidor');
      } else {
        alert('Erro ao fazer login: ' + error.message);
      }
    }
  };

  const prepend2 = () => {
    swiperRef.prependSlide([
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
    ]);
  };

  const prepend = () => {
    swiperRef.prependSlide(
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>'
    );
  };

  const append = () => {
    swiperRef.appendSlide(
      '<div class="swiper-slide">Slide ' + ++appendNumber + '</div>'
    );
  };

  const append2 = () => {
    swiperRef.appendSlide([
      '<div class="swiper-slide">Slide ' + ++appendNumber + '</div>',
      '<div class="swiper-slide">Slide ' + ++appendNumber + '</div>',
    ]);
  };

  const slides = [
    { id: 1},
    { id: 2},
    { id: 3},
    { id: 4},
    { id: 5},
  ];

  return (
    <>
      <Swiper
        onSwiper={setSwiperRef}
        slidesPerView={3}
        spaceBetween={30}
        navigation={true}
        pagination={{
          type: 'fraction',
        }}
        modules={[Pagination, Navigation]}
      >
                <SwiperSlide>Slide 1</SwiperSlide>
        <SwiperSlide>Slide 2</SwiperSlide>
        <SwiperSlide>Slide 3</SwiperSlide>
        <SwiperSlide>Slide 4</SwiperSlide>
        ))}
      </Swiper>

      <p className="append-buttons">
        <button onClick={() => prepend2()} className="prepend-2-slides">
          Prepend 2 Slides
        </button>
        <button onClick={() => prepend()} className="prepend-slide">
          Prepend Slide
        </button>
        <button onClick={() => append()} className="append-slide">
          Append Slide
        </button>
        <button onClick={() => append2()} className="append-2-slides">
          Append 2 Slides
        </button>
      </p>
    </>
  );

}
