import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Form, Input, Button, BoldLink} from './LoginStyle';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    console.log("Formulário enviado:", {email: email, password: password});
    e.preventDefault();
    try {
      const response = await axios.post('https://projetoinicialusr.onrender.com/v1/auth/login', {email: email, password: password}, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response) {
        alert('Login bem-sucedido!');
        navigate('/app');
      } else {
        alert('Email ou senha inválida');
      }
    } catch (error) {
      console.error('Erro ao fazer login:', error.response ? error.response.data : error.message);
      alert('Erro ao fazer login: ' + (error.response ? error.response.data : error.message));
    }
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
          <label>
              Email:
              <Input 
                  type="email" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  required 
              />
          </label>
          <label>
              Senha:
              <Input 
                  type="password" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  required 
              />
          </label>
          <Button type="submit">Login</Button>
          <label>
          Não tem conta? <BoldLink to="/create-client">Criar Cliente</BoldLink>
        </label>
      </Form>
        </Container>
  );
};

export default Login;
