import React, { useRef, useState } from 'react';

import { Container, CustomSwiperSlide, AddButton, SlideContent, Form, ContainerDoc } from './CarroselStyle';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

export function SwiperNav() {
  const [nomedoc, setNomeDoc] = useState('');
  const [link, setLink] = useState('');

  const handleAddPage = () => {
    const nomeDocInput = prompt('Digite o nome da p치gina:');
    const linkInput = prompt('Digite o link da p치gina:');
    if (nomeDocInput && linkInput) {
      setNomeDoc(nomeDocInput);
      setLink(linkInput);
      handleSubmit();
    }
  };

  const handleSubmit = async () => {
    const accessToken = localStorage.getItem('accessToken');
    const decodedToken = jwtDecode(accessToken);
    const email = decodedToken.sub;
    try {
      const response = await axios.patch(
        `https://gerenciador-tarefas-yuip.onrender.com/v1/users/add-doc?email=${email}`,
        { nomedoc, link },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
