import React, { useRef, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

import '../../styles.css';

import { Pagination, Navigation } from 'swiper/modules';

import { CustomSwiperSlide, AddButton, SlideContent, Form } from './CarroselStyle';

export function SwiperNav() {
  const [swiperRef, setSwiperRef] = useState(null);
  const [slides, setSlides] = useState([
    { id: 1, name: 'Slide 1', link: 'https://example.com' },
    { id: 2, name: 'Slide 2', link: 'https://example.com' },
    { id: 3, name: 'Slide 3', link: 'https://example.com' },
    { id: 4, name: 'Slide 4', link: 'https://example.com' }
  ]);

  const handleAddPage = () => {
    const name = prompt('Digite o nome da página:');
    const link = prompt('Digite o link da página:');
    if (name && link) {
      const newSlide = {
        id: slides.length + 1,
        name: name,
        link: link
      };
      setSlides([...slides, newSlide]);
    }
  };

  let prependNumber = 1;

  const prepend2 = () => {
    swiperRef.prependSlide([
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
    ]);
  };

  const prepend = () => {
    swiperRef.prependSlide(
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>'
    );
  };

  return (
    <>
      <Swiper
        slidesPerView={3}
        spaceBetween={30}
        navigation={true}
        modules={[Pagination, Navigation]}
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
            <CustomSwiperSlide>
              <a href={slide.link} target="_blank" rel="noopener noreferrer">
                <AddButton onClick={() => handleAddPage(slide.id)}>+</AddButton>
              </a>
            </CustomSwiperSlide>
          </SwiperSlide>
        ))}
      </Swiper>

      <Form isOpen={formOpen} onSubmit={handleSubmitForm}>
        <label>
          Nome da Página:
          <input type="text" value={newSlideName} onChange={(e) => setNewSlideName(e.target.value)} required />
        </label>
        <label>
          Link da Página:
          <input type="text" value={newSlideLink} onChange={(e) => setNewSlideLink(e.target.value)} required />
        </label>
        <button type="submit">Salvar</button>
      </Form>
    </>
  );

}
