import React, { useRef, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

import '../../styles.css';

import { Pagination, Navigation } from 'swiper/modules';

import { CustomSwiperSlide, AddButton, SlideContent, Form } from './CarroselStyle';

export function SwiperNav() {
  let prependNumber = 1;
  const [swiperRef, setSwiperRef] = useState(null);
  const [nomeDoc, setNomeDoc] = useState('');
  const [link, setLink] = useState('');

  const handleAddPage = (slideId) => {
    const name = prompt('Digite o nome da p치gina:');
    const link = prompt('Digite o link da p치gina:');
    if (name && link) {
      const updatedSlides = slides.map((slide) =>
        slide.id === slideId ? { ...slide, name: name, link: link } : slide
      );
      setSlides(updatedSlides);
    }
  };

  const prepend2 = () => {
    swiperRef.prependSlide([
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
    ]);
  };

  const prepend = () => {
    swiperRef.prependSlide(
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>'
    );
  };

  return (
    <>
      <Swiper
        slidesPerView={3}
        spaceBetween={30}
        navigation={true}
        modules={[Pagination, Navigation]}
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
              <a href={slide.link} target="_blank" rel="noopener noreferrer"/>
              <AddButton onClick={() => handleAddPage(slide.id)}>+</AddButton>
          </SwiperSlide>
        ))}
      </Swiper>
    </>
  );

}
