import React, { useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import '../../styles.css';
import { Pagination, Navigation } from 'swiper/modules';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

export function SwiperNav() {
  const [slides, setSlides] = useState([
    { id: 1 },
    { id: 2 },
    { id: 3 },
    { id: 4 },
    { id: 5 },
  ]);

  const handleAddPage = () => {
    const nomeDocInput = prompt('Digite o nome da p치gina:');
    const linkInput = prompt('Digite o link da p치gina:');
    if (nomeDocInput && linkInput) {
      const newSlideId = slides.length + 1;
      const newSlides = [...slides, { id: newSlideId }];
      setSlides(newSlides);
      handleSubmit(newSlideId, nomeDocInput, linkInput);
    }
  };

  const handleSubmit = async (slideId, nomeDoc, link) => {
    const accessToken = localStorage.getItem('accessToken');
    const decodedToken = jwtDecode(accessToken);
    const email = decodedToken.sub;
    try {
      const response = await axios.patch(
        `https://gerenciador-tarefas-yuip.onrender.com/v1/users/add-doc?email=${email}`,
        { slideId, nomeDoc, link },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
    } catch (error) {
      if (error.response) {
        alert('Erro ao fazer login: ' + error.response.data.message);
      } else if (error.request) {
        alert('Erro ao fazer login: Sem resposta do servidor');
      } else {
        alert('Erro ao fazer login: ' + error.message);
      }
    }
  };

  return (
    <>
      <Swiper
        slidesPerView={3}
        spaceBetween={30}
        navigation={true}
        pagination={{ type: 'fraction' }}
        modules={[Pagination, Navigation]}
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
            <div className="swiper-slide">Slide {slide.id}</div>
          </SwiperSlide>
        ))}
      </Swiper>

      <p className="append-buttons">
        <button onClick={() => handleAddPage()} className="add-slide">
          Adicionar Slide
        </button>
      </p>
    </>
  );
}
