import React, { useRef, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import { Pagination, Navigation } from 'swiper/modules';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import { CustomSwiperSlide, AddButton } from './CarroselStyle';

export function SwiperNav() {
  const [swiperRef, setSwiperRef] = useState(null);
  const [nomeDoc, setNomeDoc] = useState('');
  const [link, setLink] = useState('');

  const handleAddPage = () => {
    const nomeDocInput = prompt('Digite o nome da página:');
    const linkInput = prompt('Digite o link da página:');
    if (nomeDocInput && linkInput) {
      setNomeDoc(nomeDocInput);
      setLink(linkInput);
      handleSubmit();
    }
  };

  const handleSubmit = async () => {
    const accessToken = localStorage.getItem('accessToken');
    const decodedToken = jwtDecode(accessToken);
    const clientId = decodedToken.sub;

    try {
      const response = await axios.patch(
        'https://gerenciador-tarefas-yuip.onrender.com/v1/users/add-doc',
        { nomeDoc, link, clientId },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
      // Aqui você pode lidar com a resposta do servidor, se necessário
      console.log('Documento adicionado com sucesso:', response.data);
    } catch (error) {
      if (error.response) {
        alert('Erro ao adicionar documento: ' + error.response.data.message);
      } else if (error.request) {
        alert('Erro ao adicionar documento: Sem resposta do servidor');
      } else {
        alert('Erro ao adicionar documento: ' + error.message);
      }
    }
  };

  let prependNumber = 1;

  const prepend2 = () => {
    swiperRef.prependSlide([
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>',
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>'
    ]);
  };

  const prepend = () => {
    swiperRef.prependSlide(
      '<div class="swiper-slide">Slide ' + --prependNumber + '</div>'
    );
  };

  // Suponho que `slides` deve ser inicializado com algum valor
  const slides = [
    { id: 1, link: 'https://example.com' },
    { id: 2, link: 'https://example.com' },
    { id: 3, link: 'https://example.com' },
    { id: 4, link: 'https://example.com' }
  ];

  return (
    <>
      <Swiper
        slidesPerView={3}
        spaceBetween={30}
        navigation={true}
        modules={[Pagination, Navigation]}
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
            <CustomSwiperSlide>
              <a href={slide.link} target="_blank" rel="noopener noreferrer">
                Abrir Link
              </a>
              <AddButton onClick={() => handleAddPage(slide.id)}>+</AddButton>
            </CustomSwiperSlide>
          </SwiperSlide>
        ))}
      </Swiper>
    </>
  );
}
