import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://projetoinicialusr.onrender.com/v1/auth/login', {
        email,
        password,
      });
      const token = response.data;
      localStorage.setItem('authToken', token); // Salve o token no local storage
      console.log('Token:', token);
      alert('Login bem-sucedido!');
    } catch (error) {
      console.error('Erro ao fazer login:', error.response ? error.response.data : error.message);
      alert('Erro ao fazer login: ' + (error.response ? error.response.data : error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      </label>
      <label>
        Senha:
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      </label>
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
